// 计算缩小数组的值
const reducedArray = [];
for (let i = 0; i < newSize; i++) {
    for (let j = 0; j < newSize; j++) {
        const startRow = i * stepSize; // 原始数组每隔 stepSize 个元素取一个，以达到缩小效果
        const startCol = j * stepSize;
        let sum = 0;

        // 计算缩小数组元素的平均值
        for (let k = 0; k < stepSize; k++) {
            for (let l = 0; l < stepSize; l++) {
                sum += chunkedArray[startRow + k][startCol + l];
            }
        }

        const average = sum / (stepSize * stepSize);
        reducedArray.push(Math.floor(average));
    }
}

